public class PosNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int num=8;
    		  if(num<0){
    			  System.out.println("Number is Negative");
    		  }
    		  else if(num>0){
    			  System.out.println("Number is Positive");
    		  }
    		  else{
    			  System.out.println("Number is Zero");
    		  }
	}

}
